            // Verify the form is properly centered and not squeezed to one side
            const containerWidth = boundingBox.width;
            const containerLeft = boundingBox.x;
            const viewportCenter = viewport.width / 2;
            const containerCenter = containerLeft + (containerWidth / 2);
            const centerOffset = Math.abs(containerCenter - viewportCenter);

            // Check if text is readable (not too small)
            const titleElement = await page.locator('h3, h4, h5').first();
            const titleStyles = await titleElement.evaluate(el => {
                const styles = window.getComputedStyle(el);
                return {
                    fontSize: styles.fontSize,
                    lineHeight: styles.lineHeight
                };
            });

            const result = {
                viewport: viewport.name,
                status: 'success',
                containerWidth,
                centerOffset: Math.round(centerOffset),
                titleFontSize: titleStyles.fontSize,
                screenshot: screenshotPath,
                isCentered: centerOffset < 50, // Allow 50px tolerance
                isResponsive: containerWidth > 300 && containerWidth < viewport.width * 0.9
            };

            testResults.push(result);
            console.log(`âœ… ${viewport.name}: Container width ${containerWidth}px, centered (offset: ${result.centerOffset}px)`);

            await page.close();

        } catch (error) {
            console.error(`âŒ Error testing ${viewport.name}:`, error.message);
            testResults.push({
                viewport: viewport.name,
                status: 'error',
                error: error.message
            });
        }
    }

    await browser.close();

    // Generate summary report
    const report = {
        timestamp: new Date().toISOString(),
        testResults,
        summary: {
            total: viewports.length,
            successful: testResults.filter(r => r.status === 'success').length,
            centered: testResults.filter(r => r.isCentered).length,
            responsive: testResults.filter(r => r.isResponsive).length
        }
    };

    await fs.writeFile(
        path.join(__dirname, 'responsive-test-results.json'), 
        JSON.stringify(report, null, 2)
    );

    console.log('\nðŸ“Š Test Summary:');
    console.log(`Total viewports tested: ${report.summary.total}`);
    console.log(`Successful tests: ${report.summary.successful}`);
    console.log(`Properly centered: ${report.summary.centered}`);
    console.log(`Responsive design: ${report.summary.responsive}`);

    return report;
}

if (require.main === module) {
    testResponsiveLogin().catch(console.error);
}

module.exports = { testResponsiveLogin };

