     1→import React, { createContext, useContext, useState, useCallback } from 'react';
     2→
     3→const AuthContext = createContext();
     4→
     5→export const useAuth = () => {
     6→  const context = useContext(AuthContext);
     7→  if (!context) {
     8→    throw new Error('useAuth must be used within an AuthProvider');
     9→  }
    10→  return context;
    11→};
    12→
    13→export const AuthProvider = ({ children }) => {
    14→  const [user, setUser] = useState(null);
    15→  const [isLoading, setIsLoading] = useState(false);
    16→
    17→  const login = useCallback((userData, token) => {
    18→    try {
    19→      localStorage.setItem('token', token || 'dev-token');
    20→      const userToSet = userData || { firstName: 'Admin', role: 'owner' };
    21→      setUser(userToSet);
    22→      console.log('AuthContext - Login successful:', userData);
    23→      console.log('AuthContext - User state set to:', userToSet);
    24→      console.log('AuthContext - isAuthenticated will be:', !!userToSet);
    25→    } catch (error) {
    26→      console.error('AuthContext - Login error:', error);
    27→    }
    28→  }, []);
    29→
    30→  const logout = useCallback(() => {
    31→    try {
    32→      localStorage.removeItem('token');
    33→      setUser(null);
    34→      console.log('AuthContext - Logout successful');
    35→    } catch (error) {
    36→      console.error('AuthContext - Logout error:', error);
    37→    }
    38→  }, []);
    39→
    40→  const isAuthenticated = !!user;
    41→
    42→  const value = {
    43→    user,
    44→    isAuthenticated,
    45→    isLoading,
    46→    setIsLoading,
    47→    login,
    48→    logout
    49→  };
    50→
    51→  return (
    52→    <AuthContext.Provider value={value}>
    53→      {children}
    54→    </AuthContext.Provider>
    55→  );
