import React from 'react';
import { Card, styled } from '@mui/material';
import { motion } from 'framer-motion';

const StyledGlassCard = styled(motion(Card))(({ theme, variant = 'default', depth = 1 }) => {
  const glassConfig = {
    default: {
      background: theme.palette.mode === 'light' 
        ? 'rgba(255, 255, 255, 0.08)' 
        : 'rgba(30, 41, 59, 0.1)',
      border: theme.palette.mode === 'light'
        ? 'rgba(255, 255, 255, 0.2)'
        : 'rgba(148, 163, 184, 0.1)',
      backdropFilter: 'blur(20px) saturate(180%)',
      boxShadow: theme.palette.mode === 'light'
        ? '0 8px 32px rgba(0, 0, 0, 0.08)'
        : '0 8px 32px rgba(0, 0, 0, 0.3)',
    },
    elevated: {
      background: theme.palette.mode === 'light' 
        ? 'rgba(255, 255, 255, 0.12)' 
        : 'rgba(30, 41, 59, 0.15)',
      border: theme.palette.mode === 'light'
        ? 'rgba(255, 255, 255, 0.3)'
        : 'rgba(148, 163, 184, 0.15)',
      backdropFilter: 'blur(24px) saturate(200%)',
      boxShadow: theme.palette.mode === 'light'
        ? '0 12px 40px rgba(0, 0, 0, 0.12)'
        : '0 12px 40px rgba(0, 0, 0, 0.4)',
    },
    subtle: {
      background: theme.palette.mode === 'light' 
        ? 'rgba(255, 255, 255, 0.05)' 
        : 'rgba(30, 41, 59, 0.08)',
      border: theme.palette.mode === 'light'
        ? 'rgba(255, 255, 255, 0.15)'
        : 'rgba(148, 163, 184, 0.08)',
      backdropFilter: 'blur(16px) saturate(150%)',
      boxShadow: theme.palette.mode === 'light'
        ? '0 4px 20px rgba(0, 0, 0, 0.05)'
        : '0 4px 20px rgba(0, 0, 0, 0.2)',
    },
    frosted: {
      background: theme.palette.mode === 'light' 
        ? 'rgba(255, 255, 255, 0.15)' 
        : 'rgba(30, 41, 59, 0.2)',
      border: theme.palette.mode === 'light'
        ? 'rgba(255, 255, 255, 0.4)'
        : 'rgba(148, 163, 184, 0.2)',
      backdropFilter: 'blur(30px) saturate(220%)',
      boxShadow: theme.palette.mode === 'light'
        ? '0 16px 48px rgba(0, 0, 0, 0.15)'
        : '0 16px 48px rgba(0, 0, 0, 0.5)',
    },
  };

  const config = glassConfig[variant];

  return {
    background: config.background,
    backdropFilter: config.backdropFilter,
    WebkitBackdropFilter: config.backdropFilter,
    border: `1px solid ${config.border}`,
    borderRadius: 16,
    boxShadow: config.boxShadow,
    position: 'relative',
    overflow: 'hidden',

    // Add gradient overlay for depth
    '&::before': {
      content: '""',
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      height: '1px',
      background: theme.palette.mode === 'light'
        ? 'linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.4), transparent)'
        : 'linear-gradient(90deg, transparent, rgba(148, 163, 184, 0.2), transparent)',
      pointerEvents: 'none',
    },

    // Enhanced hover effects
    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
    '&:hover': {
      transform: `translateY(-${depth * 2}px)`,
      background: theme.palette.mode === 'light' 
        ? 'rgba(255, 255, 255, 0.12)' 
        : 'rgba(30, 41, 59, 0.15)',
      borderColor: theme.palette.mode === 'light'
        ? 'rgba(255, 255, 255, 0.3)'
        : 'rgba(148, 163, 184, 0.15)',
      boxShadow: theme.palette.mode === 'light'
        ? `0 ${8 + depth * 4}px ${32 + depth * 8}px rgba(0, 0, 0, 0.12)`
        : `0 ${8 + depth * 4}px ${32 + depth * 8}px rgba(0, 0, 0, 0.4)`,
    },

    // Support for webkit browsers
    '@supports not (backdrop-filter: blur(20px))': {
      background: theme.palette.mode === 'light'
        ? 'rgba(255, 255, 255, 0.9)'
        : 'rgba(30, 41, 59, 0.9)',
    },
  };
});

const GlassCard = React.forwardRef(({ 
  children, 
  variant = 'default',
  depth = 1,
  initial = { opacity: 0, y: 20 },
  animate = { opacity: 1, y: 0 },
  transition = { duration: 0.5, ease: [0.4, 0, 0.2, 1] },
  ...props 
}, ref) => {
  return (
    <StyledGlassCard
      ref={ref}
      variant={variant}
      depth={depth}
      initial={initial}
      animate={animate}
      transition={transition}
      {...props}
    >
      {children}
    </StyledGlassCard>
  );
});

GlassCard.displayName = 'GlassCard';

export default GlassCard;

