      : 'rgba(96, 165, 250, 0.1)',
    border: mode === 'light'
      ? 'rgba(59, 130, 246, 0.2)'
      : 'rgba(96, 165, 250, 0.2)',
    backdropFilter: 'blur(20px) saturate(180%)',
  }),
