        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          {change > 0 ? (
            <TrendingUp sx={{ color: 'success.main', fontSize: 18 }} />
          ) : (
            <TrendingDown sx={{ color: 'error.main', fontSize: 18 }} />
          )}
          <Typography 
            variant="caption" 
            sx={{ 
              color: change > 0 ? 'success.main' : 'error.main',
              fontWeight: 600,
              fontSize: '0.8rem'
            }}
          >
            {Math.abs(change)}% from last period
          </Typography>
        </Box>
      </CardContent>
    </GlassCard>
  );

  // Status Widget Component
  const StatusWidget = ({ title, count, total, icon, color, onClick }) => (
    <Slide direction="up" in={mounted} timeout={600}>
      <Card 
        sx={{ 
          height: '100%',
          background: `linear-gradient(135deg, ${color}10 0%, ${color}05 100%)`,
          border: `1px solid ${color}20`,
          borderRadius: 3,
          cursor: 'pointer',
          transition: 'all 0.3s ease',
          '&:hover': {
            transform: 'translateY(-2px)',
            boxShadow: `0 6px 20px ${color}20`,
          }
        }}
        onClick={onClick}
      >
        <CardContent sx={{ p: 2.5 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
            <Avatar 
              sx={{ 
                bgcolor: color,
                width: 40,
                height: 40,
                boxShadow: `0 3px 8px ${color}30`
              }}
            >
              {icon}
            </Avatar>
            <Box sx={{ flex: 1 }}>
              <Typography variant="h6" component="div" sx={{ fontWeight: 600 }}>
                {count}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {title}
              </Typography>
            </Box>
          </Box>

          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <LinearProgress 
              variant="determinate" 
              value={(count / total) * 100} 
              sx={{ 
                flex: 1,
                height: 6,
                borderRadius: 3,
                bgcolor: `${color}20`,
                '& .MuiLinearProgress-bar': {
                  bgcolor: color,
                  borderRadius: 3,
                }
              }} 
            />
            <Typography variant="caption" color="text.secondary">
              {Math.round((count / total) * 100)}%
            </Typography>
          </Box>
        </CardContent>
      </Card>
    </Slide>
  );

  return (
    <Fade in={mounted} timeout={500}>
      <Box sx={{ 
        p: { xs: 2, md: 3 }, 
        pb: 10,
        minHeight: '100vh',
        background: 'transparent',
        position: 'relative',
        zIndex: 1
      }}>
        {/* Header Section */}
        <GlassCard
          variant="frosted"
          depth={1}
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, ease: [0.4, 0, 0.2, 1] }}
          sx={{ 
            p: { xs: 2, md: 3 },
            mb: 3,
          }}
        >
            <Box sx={{ 
              display: 'flex', 
              flexDirection: { xs: 'column', md: 'row' },
              justifyContent: 'space-between', 
              alignItems: { xs: 'flex-start', md: 'center' }, 
              gap: 2
            }}>
              <Box>
                <Typography 
                  variant="h4" 
                  component="h1" 
                  sx={{ 
                    fontWeight: 700,
                    background: 'linear-gradient(135deg, #1e40af 0%, #10b981 100%)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    backgroundClip: 'text',
                    mb: 1
                  }}
                >
                  Dashboard
                </Typography>
                <Typography variant="body1" color="text.secondary">
                  Welcome back, {user?.firstName || 'User'}! Here's what's happening today.
                </Typography>
              </Box>

              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <ThemeToggle />

                <Tooltip title="Refresh">
                  <IconButton 
                    onClick={handleRefresh} 
                    disabled={isLoading}
                    sx={{
                      bgcolor: 'rgba(30, 64, 175, 0.1)',
                      backdropFilter: 'blur(10px)',
                      border: '1px solid rgba(30, 64, 175, 0.2)',
                      '&:hover': { 
                        bgcolor: 'rgba(30, 64, 175, 0.2)',
                        transform: 'scale(1.05)'
                      },
                      transition: 'all 0.2s ease-in-out'
                    }}
                  >
                    <Refresh sx={{ 
                      animation: isLoading ? 'spin 1s linear infinite' : 'none',
                      '@keyframes spin': {
                        '0%': { transform: 'rotate(0deg)' },
                        '100%': { transform: 'rotate(360deg)' }
                      }
                    }} />
                  </IconButton>
                </Tooltip>

                <Chip
                  icon={<CheckCircle />}
                  label="Connected"
                  color="success"
                  size="small"
                  sx={{ 
                    cursor: 'pointer',
                    bgcolor: 'rgba(16, 185, 129, 0.1)',
                    border: '1px solid rgba(16, 185, 129, 0.3)',
                    backdropFilter: 'blur(10px)',
                    '&:hover': {
                      bgcolor: 'rgba(16, 185, 129, 0.15)',
                      transform: 'scale(1.02)'
                    },
                    transition: 'all 0.2s ease-in-out'
                  }}
                />
              </Box>
            </Box>
          </Paper>
        </Grow>

        {/* Timeframe Selector */}
        <Slide direction="up" in={mounted} timeout={500}>
          <Paper 
            elevation={0}
            sx={{ 
              p: 2,
              mb: 3,
              background: 'rgba(255, 255, 255, 0.6)',
              backdropFilter: 'blur(10px)',
              borderRadius: 3,
              border: '1px solid rgba(255, 255, 255, 0.2)'
            }}
          >
            <Stack direction="row" spacing={1} sx={{ flexWrap: 'wrap', gap: 1 }}>
              {['today', 'week', 'month', 'quarter'].map((timeframe) => (
                <Button
                  key={timeframe}
                  variant={selectedTimeframe === timeframe ? 'contained' : 'outlined'}
                  size="small"
                  onClick={() => handleTimeframeChange(timeframe)}
                  sx={{
                    borderRadius: 2,
                    textTransform: 'capitalize',
                    fontWeight: 600,
                    ...(selectedTimeframe === timeframe && {
                      background: 'linear-gradient(135deg, #1e40af 0%, #10b981 100%)',
                      '&:hover': {
                        background: 'linear-gradient(135deg, #1e3a8a 0%, #059669 100%)',
                      }
                    })
                  }}
                >
                  {timeframe.charAt(0).toUpperCase() + timeframe.slice(1)}
                </Button>
              ))}
            </Stack>
          </Paper>
        </Slide>

        {/* KPI Cards Grid */}
        <Grid container spacing={3} sx={{ mb: 4 }}>
          <Grid item xs={12} sm={6} lg={3}>
            <ModernKPICard
              title="Total Revenue"
              value={`$${dashboardData.kpis.revenue.toLocaleString()}`}
              change={12.5}
              icon={<AttachMoney />}
              color={theme.palette.primary.main}
              onClick={() => handleKPIWidgetClick('Revenue')}
            />
          </Grid>

          <Grid item xs={12} sm={6} lg={3}>
            <ModernKPICard
              title="Jobs in Progress"
              value={dashboardData.kpis.jobsInProgress}
              change={-2.3}
              icon={<Build />}
              color={theme.palette.warning.main}
              onClick={() => handleKPIWidgetClick('Jobs')}
            />
          </Grid>

          <Grid item xs={12} sm={6} lg={3}>
            <ModernKPICard
              title="Avg Cycle Time"
              value={`${dashboardData.kpis.avgCycleTime} days`}
              change={8.1}
              icon={<Speed />}
              color={theme.palette.info.main}
              onClick={() => handleKPIWidgetClick('Cycle Time')}
            />
          </Grid>

          <Grid item xs={12} sm={6} lg={3}>
            <ModernKPICard
              title="Customer Satisfaction"
              value={`${dashboardData.kpis.customerSatisfaction}%`}
              change={5.2}
              icon={<VerifiedUser />}
              color={theme.palette.success.main}
              onClick={() => handleKPIWidgetClick('Satisfaction')}
            />
          </Grid>
        </Grid>

        {/* Main Content Grid */}
        <Grid container spacing={3}>
          {/* Left Column */}
          <Grid item xs={12} lg={8}>
            {/* Production Status */}
            <Slide direction="up" in={mounted} timeout={700}>
              <Paper 
                elevation={0}
                sx={{ 
                  p: 3,
                  mb: 3,
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.2)'
                }}
              >
                <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 3 }}>
                  <Typography variant="h6" sx={{ fontWeight: 600 }}>
                    Production Status
                  </Typography>
                  <Button
                    endIcon={<ArrowForward />}
                    onClick={handleViewFullBoard}
                    sx={{ textTransform: 'none', fontWeight: 600 }}
                  >
                    View Full Board
                  </Button>
                </Box>

                <Grid container spacing={2}>
                  <Grid item xs={6} sm={3}>
                    <StatusWidget
                      title="Estimate"
                      count={dashboardData.productionStats.estimate}
                      total={dashboardData.productionStats.total}
                      icon={<Assignment />}
                      color={theme.palette.info.main}
                      onClick={() => handleStatusWidgetClick('Estimate')}
                    />
                  </Grid>
                  <Grid item xs={6} sm={3}>
                    <StatusWidget
                      title="In Progress"
                      count={dashboardData.productionStats.inProgress}
                      total={dashboardData.productionStats.total}
                      icon={<Engineering />}
                      color={theme.palette.warning.main}
                      onClick={() => handleStatusWidgetClick('In Progress')}
                    />
                  </Grid>
                  <Grid item xs={6} sm={3}>
                    <StatusWidget
                      title="Quality Check"
                      count={dashboardData.productionStats.qualityCheck}
                      total={dashboardData.productionStats.total}
                      icon={<VerifiedUser />}
                      color={theme.palette.primary.main}
                      onClick={() => handleStatusWidgetClick('Quality Check')}
                    />
                  </Grid>
                  <Grid item xs={6} sm={3}>
                    <StatusWidget
                      title="Ready Pickup"
                      count={dashboardData.productionStats.readyPickup}
                      total={dashboardData.productionStats.total}
                      icon={<LocalShipping />}
                      color={theme.palette.success.main}
                      onClick={() => handleStatusWidgetClick('Ready Pickup')}
                    />
                  </Grid>
                </Grid>
              </Paper>
            </Slide>

            {/* Recent Jobs */}
            <Slide direction="up" in={mounted} timeout={800}>
              <Paper 
                elevation={0}
                sx={{ 
                  p: 3,
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.2)'
                }}
              >
                <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                  Recent Jobs
                </Typography>
                <RecentJobs jobs={recentJobs} />
              </Paper>
            </Slide>
          </Grid>

          {/* Right Column */}
          <Grid item xs={12} lg={4}>
            {/* Quick Actions */}
            <Slide direction="left" in={mounted} timeout={600}>
              <Paper 
                elevation={0}
                sx={{ 
                  p: 3,
                  mb: 3,
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.2)'
                }}
              >
                <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                  Quick Actions
                </Typography>
                <QuickActions />
              </Paper>
            </Slide>

            {/* Alerts */}
            <Slide direction="left" in={mounted} timeout={700}>
              <Paper 
                elevation={0}
                sx={{ 
                  p: 3,
                  mb: 3,
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.2)'
                }}
              >
                <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                  Alerts
                </Typography>
                <AlertsPanel alerts={alerts} />
              </Paper>
            </Slide>

            {/* Weather Widget */}
            <Slide direction="left" in={mounted} timeout={800}>
              <Paper 
                elevation={0}
                sx={{ 
                  p: 3,
                  mb: 3,
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.2)'
                }}
              >
                <WeatherWidget />
              </Paper>
            </Slide>

            {/* Calendar Widget */}
            <Slide direction="left" in={mounted} timeout={900}>
              <Paper 
                elevation={0}
                sx={{ 
                  p: 3,
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.2)'
                }}
              >
                <CalendarWidget />
              </Paper>
            </Slide>
          </Grid>
        </Grid>

        {/* Advanced Analytics Section */}
        <Slide direction="up" in={mounted} timeout={1000}>
          <Box sx={{ mt: 4 }}>
            <AdvancedAnalytics />
          </Box>
        </Slide>
      </Box>
    </Fade>
  );
};

export default Dashboard;

