                <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                  Recent Jobs
                </Typography>
                <RecentJobs jobs={recentJobs} />
              </Paper>
            </Slide>
          </Grid>

          {/* Right Column */}
          <Grid item xs={12} lg={4}>
            {/* Quick Actions */}
            <Slide direction="left" in={mounted} timeout={600}>
              <Paper 
                elevation={0}
                sx={{ 
                  p: 3,
                  mb: 3,
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.2)'
                }}
              >
                <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                  Quick Actions
                </Typography>
                <QuickActions />
              </Paper>
            </Slide>

            {/* Alerts */}
            <Slide direction="left" in={mounted} timeout={700}>
              <Paper 
                elevation={0}
                sx={{ 
                  p: 3,
                  mb: 3,
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.2)'
                }}
              >
                <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                  Alerts
                </Typography>
                <AlertsPanel alerts={alerts} />
              </Paper>
            </Slide>

            {/* Weather Widget */}
            <Slide direction="left" in={mounted} timeout={800}>
              <Paper 
                elevation={0}
                sx={{ 
                  p: 3,
                  mb: 3,
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.2)'
                }}
              >
                <WeatherWidget />
              </Paper>
            </Slide>

            {/* Calendar Widget */}
            <Slide direction="left" in={mounted} timeout={900}>
              <Paper 
                elevation={0}
                sx={{ 
                  p: 3,
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.2)'
                }}
