        </Grid>

        {/* Advanced Analytics Section */}
        <Slide direction="up" in={mounted} timeout={1000}>
          <Box sx={{ mt: 4 }}>
            <AdvancedAnalytics />
          </Box>
        </Slide>
      </Box>
    </Fade>
  );
};

export default Dashboard;

