                  Alerts
                </Typography>
                <AlertsPanel alerts={alerts} />
              </Paper>
            </Slide>

            {/* Weather Widget */}
            <Slide direction="left" in={mounted} timeout={800}>
              <Paper 
                elevation={0}
                sx={{ 
                  p: 3,
                  mb: 3,
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.2)'
                }}
              >
                <WeatherWidget />
              </Paper>
            </Slide>

            {/* Calendar Widget */}
            <Slide direction="left" in={mounted} timeout={900}>
              <Paper 
                elevation={0}
                sx={{ 
                  p: 3,
                  background: 'rgba(255, 255, 255, 0.8)',
                  backdropFilter: 'blur(10px)',
                  borderRadius: 3,
                  border: '1px solid rgba(255, 255, 255, 0.2)'
                }}
              >
                <CalendarWidget />
              </Paper>
            </Slide>
          </Grid>
        </Grid>

        {/* Advanced Analytics Section */}
        <Slide direction="up" in={mounted} timeout={1000}>
          <Box sx={{ mt: 4 }}>
            <AdvancedAnalytics />
          </Box>
        </Slide>
      </Box>
    </Fade>
  );
};

export default Dashboard;

