  Dashboard as DashboardIcon
} from '@mui/icons-material';

// Components
import { KPIChart } from '../../components/Dashboard/KPIChart';
import { ProductionBoardPreview } from '../../components/Dashboard/ProductionBoardPreview';
import { RecentJobs } from '../../components/Dashboard/RecentJobs';
import { AlertsPanel } from '../../components/Dashboard/AlertsPanel';
import { QuickActions } from '../../components/Dashboard/QuickActions';
import { WeatherWidget } from '../../components/Dashboard/WeatherWidget';
import { CalendarWidget } from '../../components/Dashboard/CalendarWidget';
import AdvancedAnalytics from '../../components/Dashboard/AdvancedAnalytics';
import ModernCard from '../../components/Common/ModernCard';
import ModernStatsCard from '../../components/Dashboard/ModernStatsCard';
import ModernBackground from '../../components/Common/ModernBackground';
import GlassCard from '../../components/Common/GlassCard';
import ThemeToggle from '../../components/Common/ThemeToggle';
import { BentoGrid, BentoItem } from '../../components/Layout/BentoGrid';

// Hooks
import { useAuth } from '../../contexts/AuthContext';
import { useTheme as useAppTheme } from '../../contexts/ThemeContext';

// Utils
import { getGlassStyles, glassHoverEffects, coloredGlassVariants } from '../../utils/glassTheme';
import { microAnimations, containerAnimations } from '../../utils/animations';

const Dashboard = () => {
  const theme = useTheme();
  const { mode } = useAppTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const isTablet = useMediaQuery(theme.breakpoints.down('lg'));
  const { user } = useAuth();

  const [selectedTimeframe, setSelectedTimeframe] = useState('today');
  const [isLoading, setIsLoading] = useState(false);
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);
  }, []);

  // Mock data
  const dashboardData = {
    kpis: { 
      revenue: 123456, 
      jobsInProgress: 12, 
      avgCycleTime: 6.2, 
      customerSatisfaction: 95 
    },
    productionStats: { 
      total: 24, 
      estimate: 3, 
      inProgress: 12, 
      qualityCheck: 2, 
      readyPickup: 1 
    },
    efficiencyStats: { 
      laborEfficiency: 88, 
      partsEfficiency: 92, 
      bayUtilization: 73 
    }
  };

  const recentJobs = [
    { id: 1, jobNumber: 'JOB-001', customerName: 'John Smith', status: 'estimate', priority: 'high' },
    { id: 2, jobNumber: 'JOB-002', customerName: 'Jane Doe', status: 'in_progress', priority: 'medium' },
    { id: 3, jobNumber: 'JOB-003', customerName: 'Bob Johnson', status: 'ready_pickup', priority: 'low' }
  ];

  const alerts = [
    { id: 1, title: 'Parts Overdue', message: '2 parts overdue', type: 'warning' },
    { id: 2, title: 'Job Overdue', message: 'One job overdue', type: 'error' }
  ];

  const handleRefresh = () => {
    setIsLoading(true);
    setTimeout(() => setIsLoading(false), 1000);
  };

  const handleTimeframeChange = (timeframe) => {
    setSelectedTimeframe(timeframe);
  };

  const handleViewFullBoard = () => {
    // Navigation logic here
  };

  const handleKPIWidgetClick = (title) => {
    // KPI widget click logic
  };

  const handleStatusWidgetClick = (title) => {
    // Status widget click logic
  };

  // Enhanced KPI Card Component with glassmorphism
  const ModernKPICard = ({ title, value, change, icon, color, onClick }) => (
    <GlassCard
      variant="elevated"
      depth={2}
      initial={{ opacity: 0, y: 20, scale: 0.95 }}
      animate={{ opacity: 1, y: 0, scale: 1 }}
      transition={{ duration: 0.6, ease: [0.4, 0, 0.2, 1] }}
      sx={{ 
        height: '100%',
        cursor: 'pointer',
        position: 'relative',
        overflow: 'hidden',
        ...getGlassStyles('elevated', mode),
        ...glassHoverEffects(mode, 2),

        // Color-specific glass tinting
        background: color ? `linear-gradient(135deg, ${color}08 0%, ${color}03 100%), ${getGlassStyles('elevated', mode).background}` : undefined,
        borderColor: color ? `${color}30` : undefined,

        '&::after': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: `radial-gradient(circle at 20% 20%, ${color}15 0%, transparent 50%)`,
          pointerEvents: 'none',
        },

        '&:hover': {
          ...glassHoverEffects(mode, 2)['&:hover'],
          borderColor: color,
          '&::after': {
            background: `radial-gradient(circle at 20% 20%, ${color}20 0%, transparent 60%)`,
          }
        }
      }}
      onClick={onClick}
    >
      <CardContent sx={{ p: 3, position: 'relative', zIndex: 1 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
          <Avatar 
            sx={{ 
              bgcolor: color,
              width: 52,
              height: 52,
              boxShadow: `0 8px 24px ${color}40`,
              backdropFilter: 'blur(10px)',
              border: `1px solid ${color}30`
            }}
          >
            {icon}
          </Avatar>
          <IconButton 
            size="small" 
            sx={{ 
              color: 'text.secondary',
              backdropFilter: 'blur(10px)',
              '&:hover': { 
                bgcolor: 'rgba(255, 255, 255, 0.1)',
                color: color
              }
            }}
          >
            <MoreVert />
          </IconButton>
        </Box>

        <Typography variant="h4" component="div" sx={{ fontWeight: 700, mb: 1 }}>
          {value}
        </Typography>

        <Typography variant="body2" color="text.secondary" sx={{ mb: 2, fontWeight: 500 }}>
          {title}
        </Typography>

        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          {change > 0 ? (
            <TrendingUp sx={{ color: 'success.main', fontSize: 18 }} />
          ) : (
            <TrendingDown sx={{ color: 'error.main', fontSize: 18 }} />
          )}
          <Typography 
            variant="caption" 
            sx={{ 
              color: change > 0 ? 'success.main' : 'error.main',
              fontWeight: 600,
              fontSize: '0.8rem'
            }}
          >
            {Math.abs(change)}% from last period
          </Typography>
        </Box>
      </CardContent>
    </GlassCard>
  );

  // Status Widget Component
  const StatusWidget = ({ title, count, total, icon, color, onClick }) => (
    <Slide direction="up" in={mounted} timeout={600}>
      <Card 
        sx={{ 
          height: '100%',
          background: `linear-gradient(135deg, ${color}10 0%, ${color}05 100%)`,
          border: `1px solid ${color}20`,
          borderRadius: 3,
          cursor: 'pointer',
          transition: 'all 0.3s ease',
          '&:hover': {
            transform: 'translateY(-2px)',
            boxShadow: `0 6px 20px ${color}20`,
