          <BentoItem area="sidebar1" variant="compact">
            <GlassCard
              variant="subtle"
              sx={{ 
                p: 3,
                height: '100%',
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                Quick Actions
