              <QuickActions />
            </GlassCard>
          </BentoItem>

          {/* Alerts */}
          <BentoItem area="sidebar2" variant="compact">
            <GlassCard
              variant="subtle"
              sx={{ 
                p: 3,
                height: '100%',
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                Alerts
              </Typography>
              <AlertsPanel alerts={alerts} />
            </GlassCard>
          </BentoItem>

          {/* Weather Widget */}
          <BentoItem area="weather" variant="compact">
            <GlassCard
              variant="default"
              sx={{ 
                p: 3,
                height: '100%'
              }}
            >
              <WeatherWidget />
            </GlassCard>
          </BentoItem>

          {/* Calendar Widget */}
          <BentoItem area="calendar" variant="compact">
            <GlassCard
              variant="default"
              sx={{ 
                p: 3,
                height: '100%'
              }}
            >
              <CalendarWidget />
            </GlassCard>
          </BentoItem>

          {/* AI Insights Placeholder */}
          <BentoItem area="ai-insights" variant="wide">
            <GlassCard
              variant="frosted"
              sx={{ 
                p: 3,
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.1) 0%, rgba(16, 185, 129, 0.1) 100%)'
              }}
            >
              <Box sx={{ textAlign: 'center' }}>
                <Assessment sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
                <Typography variant="h6" sx={{ fontWeight: 600, mb: 1 }}>
                  AI Insights
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Coming Soon - Smart recommendations based on your data
                </Typography>
              </Box>
            </GlassCard>
          </BentoItem>

        {/* Advanced Analytics Section */}
        <Slide direction="up" in={mounted} timeout={1000}>
          <Box sx={{ mt: 4 }}>
            <AdvancedAnalytics />
          </Box>
        </Slide>
      </Box>
    </Fade>
  );
};

export default Dashboard;

