import React, { useState, useEffect, useRef } from 'react';
import { Button, Box, TextField, Typography, Alert, CircularProgress, Paper } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { authService } from '../../services/authService';

export default function Login() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const usernameFieldRef = useRef(null);

  useEffect(() => {
    // Focus username field after component mounts
    const timer = setTimeout(() => {
      if (usernameFieldRef.current) {
        usernameFieldRef.current.focus();
      }
    }, 100);

    return () => {
      clearTimeout(timer);
    };
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setError('');
      setLoading(true);

      if (!username || !password) {
        setError('Please enter both username and password');
        return;
      }

      const response = await authService.login(username, password);

      if (response.success && response.token && response.user) {
        localStorage.setItem('token', response.token);
        login(response.user, response.token);
        navigate('/dashboard');
      } else {
        setError('Invalid username or password');
      }
    } catch (err) {
      if (err.response?.data?.error) {
        setError(err.response.data.error);
      } else if (err.message === 'Network Error') {
        setError('Cannot connect to server. Please check if the server is running.');
      } else {
        setError('Login failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  return (
    <Box sx={{
      minHeight: '100vh',
      backgroundColor: '#f5f5f5',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: 2
    }}>
      <Paper sx={{
        width: '100%',
        maxWidth: 400,
        padding: 4,
        borderRadius: 2,
        boxShadow: 3
      }}>
        <Box sx={{ textAlign: 'center', mb: 4 }}>
          <Typography variant="h4" sx={{ fontWeight: 'bold', mb: 1 }}>
            🔧 CollisionOS
          </Typography>
          <Typography variant="h6" color="text.secondary">
            Auto Body Shop Management
          </Typography>
        </Box>

        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        <Box component="form" onSubmit={handleSubmit} sx={{ width: '100%' }}>
          <TextField 
            ref={usernameFieldRef}
            label="Username" 
            fullWidth
            margin="normal"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="admin"
            variant="outlined"
            autoComplete="username"
            disabled={loading}
            sx={{ mb: 2 }}
          />

          <TextField 
            label="Password" 
            type="password" 
            fullWidth
            margin="normal"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="admin123"
            variant="outlined"
            autoComplete="current-password"
            disabled={loading}
            sx={{ mb: 3 }}
          />

          <Button 
            type="submit"
            variant="contained" 
            disabled={loading}
            size="large"
            fullWidth
            sx={{ 
              py: 1.5,
              fontSize: '1rem',
              fontWeight: 600
            }}
            startIcon={loading ? <CircularProgress size={20} color="inherit" /> : null}
          >
            {loading ? 'Signing In...' : 'Login'}
          </Button>
        </Box>

        <Box sx={{ 
          mt: 3,
          p: 2,
          backgroundColor: '#f9f9f9',
          borderRadius: 1,
          border: '1px solid #e0e0e0'
        }}>
          <Typography variant="caption" display="block" sx={{ fontWeight: 600, mb: 1 }}>
            Demo Accounts:
          </Typography>
          <Typography variant="caption" display="block">
            admin / admin123 (Owner)
          </Typography>
          <Typography variant="caption" display="block">
            manager / manager123 (Manager)
          </Typography>
          <Typography variant="caption" display="block">
            estimator / estimator123 (Estimator)
          </Typography>
        </Box>
      </Paper>
    </Box>
  );
}

