import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { CssBaseline, Box } from '@mui/material';
import { QueryClient, QueryClientProvider } from 'react-query';
import { Toaster } from 'react-hot-toast';

// Components
import Layout from './components/Layout/Layout';
import Login from './pages/Auth/Login';
import Dashboard from './pages/Dashboard/Dashboard';
import ProductionBoard from './pages/Production/ProductionBoard';
import CustomerList from './pages/Customer/CustomerList';
import PartsManagement from './pages/Parts/PartsManagement';
import TechnicianDashboard from './pages/Technician/TechnicianDashboard';
import QualityControlDashboard from './pages/QualityControl/QualityControlDashboard';
import ReportsManagement from './pages/Reports/ReportsManagement';

// Hooks
import { useAuth } from './hooks/useAuth';
import { useSocket } from './hooks/useSocket';
import { useTheme } from './hooks/useTheme';

// Store
import { useAppStore } from './store/appStore';

// Services
import { authService } from './services/authService';
import { notificationService } from './services/notificationService';

// Utils
import { ProtectedRoute } from './components/Auth/ProtectedRoute';
import { LoadingScreen } from './components/Common/LoadingScreen';

// Create React Query client
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: 1,
      refetchOnWindowFocus: false,
      staleTime: 5 * 60 * 1000, // 5 minutes
    },
  },
});

function App() {
  const { user, isAuthenticated, isLoading, login, logout } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const { socket, isConnected } = useSocket();
  const { 
    notifications, 
    addNotification, 
    removeNotification,
    setNotifications 
  } = useAppStore();

  const [isInitialized, setIsInitialized] = useState(false);

  // Initialize app
  useEffect(() => {
    const initializeApp = async () => {
      try {
        // Check for stored authentication
        const token = localStorage.getItem('token');
        if (token) {
          const userData = await authService.validateToken(token);
          if (userData) {
            login(userData, token);
          } else {
            localStorage.removeItem('token');
          }
        }

        // Load user preferences
        const savedTheme = localStorage.getItem('theme');
        if (savedTheme) {
          // Theme will be handled by useTheme hook
        }

        setIsInitialized(true);
      } catch (error) {
        console.error('App initialization error:', error);
        setIsInitialized(true);
      }
    };

    initializeApp();
  }, [login]);

  // Handle socket notifications
  useEffect(() => {
    if (!socket || !isConnected) return;

    socket.on('notification', (notification) => {
      addNotification(notification);
      notificationService.showNotification(notification);
    });

    socket.on('job_update', (data) => {
      // Handle real-time job updates
      console.log('Job update received:', data);
    });

    socket.on('production_update', (data) => {
      // Handle production board updates
      console.log('Production update received:', data);
    });

    socket.on('parts_update', (data) => {
      // Handle parts updates
      console.log('Parts update received:', data);
    });

    socket.on('financial_update', (data) => {
      // Handle financial updates
      console.log('Financial update received:', data);
    });

    return () => {
      if (socket) {
        socket.off('notification');
        socket.off('job_update');
        socket.off('production_update');
        socket.off('parts_update');
        socket.off('financial_update');
      }
    };
  }, [socket, isConnected, addNotification]);

  // Handle Electron menu events
  useEffect(() => {
    if (typeof window !== 'undefined' && window.electronAPI) {
      const handleMenuEvent = (event, data) => {
        switch (event) {
          case 'menu-new-job':
            // Navigate to new job
            break;
          case 'menu-new-customer':
            // Navigate to new customer
            break;
          case 'menu-production-board':
            // Navigate to production board
            break;
          case 'menu-financial-dashboard':
            // Navigate to financial dashboard
            break;
          case 'menu-customer-management':
            // Navigate to customer management
            break;
          case 'menu-parts-management':
            // Navigate to parts management
            break;
          case 'menu-quality-control':
            // Navigate to quality control
            break;
          case 'menu-preferences':
            // Open preferences
            break;
          case 'menu-about':
            // Show about dialog
            break;
          default:
            break;
        }
      };

      // Set up menu event listeners
      window.electronAPI.onMenuNewJob(handleMenuEvent);
      window.electronAPI.onMenuNewCustomer(handleMenuEvent);
      window.electronAPI.onMenuProductionBoard(handleMenuEvent);
      window.electronAPI.onMenuFinancialDashboard(handleMenuEvent);
      window.electronAPI.onMenuCustomerManagement(handleMenuEvent);
      window.electronAPI.onMenuPartsManagement(handleMenuEvent);
      window.electronAPI.onMenuQualityControl(handleMenuEvent);
      window.electronAPI.onMenuPreferences(handleMenuEvent);
      window.electronAPI.onMenuAbout(handleMenuEvent);

      return () => {
        // Clean up listeners
        window.electronAPI.removeAllListeners('menu-new-job');
        window.electronAPI.removeAllListeners('menu-new-customer');
        window.electronAPI.removeAllListeners('menu-production-board');
        window.electronAPI.removeAllListeners('menu-financial-dashboard');
        window.electronAPI.removeAllListeners('menu-customer-management');
        window.electronAPI.removeAllListeners('menu-parts-management');
        window.electronAPI.removeAllListeners('menu-quality-control');
        window.electronAPI.removeAllListeners('menu-preferences');
        window.electronAPI.removeAllListeners('menu-about');
      };
    }
  }, []);

  // Create Material-UI theme with auto body shop colors and professional design
  const muiTheme = createTheme({
    palette: {
      mode: theme,
      primary: {
        main: theme === 'dark' ? '#4fc3f7' : '#1565c0', // Professional blue
        light: theme === 'dark' ? '#81d4fa' : '#42a5f5',
        dark: theme === 'dark' ? '#29b6f6' : '#0d47a1',
        contrastText: '#ffffff',
      },
      secondary: {
