        light: theme === 'dark' ? '#ff8a65' : '#ff5722',
        dark: theme === 'dark' ? '#f4511e' : '#bf360c',
        contrastText: '#ffffff',
      },
      success: {
        main: theme === 'dark' ? '#66bb6a' : '#2e7d32', // Quality green
        light: theme === 'dark' ? '#81c784' : '#4caf50',
        dark: theme === 'dark' ? '#4caf50' : '#1b5e20',
      },
      warning: {
        main: theme === 'dark' ? '#ffb74d' : '#f57c00', // Attention orange
        light: theme === 'dark' ? '#ffc947' : '#ff9800',
        dark: theme === 'dark' ? '#ff9800' : '#e65100',
      },
      error: {
        main: theme === 'dark' ? '#e57373' : '#c62828', // Alert red
        light: theme === 'dark' ? '#ef5350' : '#f44336',
        dark: theme === 'dark' ? '#f44336' : '#b71c1c',
      },
      info: {
        main: theme === 'dark' ? '#64b5f6' : '#1976d2', // Information blue
        light: theme === 'dark' ? '#90caf9' : '#2196f3',
        dark: theme === 'dark' ? '#2196f3' : '#0d47a1',
      },
      background: {
        default: theme === 'dark' ? '#0a0e13' : '#f8fafc',
        paper: theme === 'dark' ? '#1a202c' : '#ffffff',
        secondary: theme === 'dark' ? '#2d3748' : '#f1f5f9',
      },
      text: {
        primary: theme === 'dark' ? '#f7fafc' : '#1a202c',
        secondary: theme === 'dark' ? '#a0aec0' : '#4a5568',
        disabled: theme === 'dark' ? '#718096' : '#a0aec0',
      },
      divider: theme === 'dark' ? 'rgba(255, 255, 255, 0.12)' : 'rgba(0, 0, 0, 0.12)',
