        if (token) {
          // For development, create a simple user object
          const userData = { firstName: 'Admin', role: 'owner' };
          setUser(userData);
        }
      } catch (error) {
        console.error('Auth initialization error:', error);
      } finally {
        setIsLoading(false);
      }
    };

    initializeAuth();
  }, []);

