test.describe('Authentication Flow', () => {
  test.beforeEach(async ({ page }) => {
    // Navigate to the app
    await page.goto('/');
  });

  test('should display login page correctly', async ({ page }) => {
    // Check if we're on login page
    await expect(page).toHaveTitle(/CollisionOS/);

    // Check for login form elements
    await expect(page.locator('input[type="email"], input[placeholder*="admin"], input[name="username"]')).toBeVisible();
    await expect(page.locator('input[type="password"]')).toBeVisible();
    await expect(page.locator('button:has-text("Login"), button[type="submit"]')).toBeVisible();

    // Check for CollisionOS branding/logo
    await expect(page.locator('text=CollisionOS')).toBeVisible();
  });

  test('should show validation errors for empty form', async ({ page }) => {
    // Try to submit empty form
