const { chromium } = require('playwright');

(async () => {
  console.log('🚗 Testing CollisionOS Authentication...');

  const browser = await chromium.launch({ headless: false });
  const page = await browser.newPage();

  try {
    // Navigate to the app
    console.log('📍 Navigating to http://localhost:3000');
    await page.goto('http://localhost:3000');

    // Wait for page to load
    await page.waitForTimeout(2000);

    console.log('📸 Taking screenshot of current state...');
    await page.screenshot({ path: 'app-state.png' });

    // Check if we're on login page
    const hasLoginForm = await page.locator('input[placeholder="admin"]').isVisible();
    const hasPasswordField = await page.locator('input[type="password"]').isVisible();
    const hasLoginButton = await page.locator('button:has-text("Login")').isVisible();

    if (hasLoginForm && hasPasswordField && hasLoginButton) {
      console.log('✅ Login page is displayed correctly');

      // Try to login
      console.log('🔑 Attempting login...');
      await page.fill('input[placeholder="admin"]', 'admin');
      await page.fill('input[type="password"]', 'admin123');
      await page.click('button:has-text("Login")');

      // Wait for redirect
      await page.waitForTimeout(3000);

      const currentUrl = page.url();
      console.log('🌐 Current URL:', currentUrl);

      if (currentUrl.includes('/dashboard') || currentUrl.includes('/app') || currentUrl !== 'http://localhost:3000/login') {
        console.log('✅ Login successful! Redirected to dashboard');

        // Check for Customer Management navigation
        const hasNavigation = await page.locator('nav, [role="navigation"]').count() > 0;
        if (hasNavigation) {
          console.log('✅ Navigation is visible');

          // Try to find customer management link
          const customerLink = await page.locator('text=Customer').first();
          if (await customerLink.isVisible()) {
            console.log('✅ Customer Management link found');
            await customerLink.click();
            await page.waitForTimeout(2000);

            const finalUrl = page.url();
            console.log('🎯 Customer page URL:', finalUrl);

            if (finalUrl.includes('/customer')) {
              console.log('🎉 SUCCESS: Customer Management page is accessible!');
            } else {
              console.log('⚠️ Customer page not accessible yet');
            }
          } else {
            console.log('⚠️ Customer Management link not found in navigation');
          }
        }

      } else {
        console.log('❌ Login failed or no redirect occurred');
      }

    } else {
      console.log('❌ Login page not displayed correctly');
    }

  } catch (error) {
    console.error('❌ Test failed:', error.message);
  } finally {
    await browser.close();
  }
})();

