    80→        p: { xs: 1, sm: 2 },
    81→        position: 'relative',
    82→        zIndex: 1,
    83→        width: '100%',
    84→        overflow: 'hidden'
    85→      }}>
    86→        <ModernCard 
    87→          variant="glass" 
    88→          hover={false}
    89→          sx={{ 
    90→            maxWidth: { xs: 'calc(100% - 16px)', sm: 420 }, 
    91→            width: '100%',
    92→            mx: { xs: 'auto', sm: 0 },
    93→            p: { xs: 1, sm: 2 },
    94→            position: 'relative',
    95→            zIndex: 2,
    96→            pointerEvents: 'auto',
    97→            maxHeight: { xs: 'fit-content', sm: 'none' },
    98→            overflow: 'visible'
    99→          }}
   100→        >
   101→          <Box sx={{ p: { xs: 1.5, sm: 4 }, display: 'flex', flexDirection: 'column', gap: { xs: 1.5, sm: 3 } }}>
   102→            <Box sx={{ textAlign: 'center', mb: 2 }}>
   103→              <Typography 
   104→                variant="h3" 
   105→                sx={{ 
   106→                  mb: 1,
   107→                  fontWeight: 700,
   108→                  background: 'linear-gradient(135deg, #1e40af 0%, #10b981 100%)',
   109→                  WebkitBackgroundClip: 'text',
   110→                  WebkitTextFillColor: 'transparent',
   111→                  backgroundClip: 'text'
   112→                }}
   113→              >
   114→                🔧 CollisionOS
   115→              </Typography>
   116→              <Typography variant="h6" sx={{ color: 'text.secondary', fontWeight: 500 }}>
