import React, { useState, useEffect } from 'react';
import { Button, Box, TextField, Typography, Alert, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { authService } from '../../services/authService';
import ModernBackground from '../../components/Common/ModernBackground';
import ModernCard from '../../components/Common/ModernCard';

export default function Login() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // Add login-page class to body on mount
    document.body.classList.add('login-page');

    // Remove class on unmount
    return () => {
      document.body.classList.remove('login-page');
    };
  }, []);

  const handleLogin = async () => {
    try {
      setError('');
      setLoading(true);

      if (!username || !password) {
        setError('Please enter both username and password');
        return;
      }

      const response = await authService.login(username, password);

      if (response.success && response.token && response.user) {
        // Store token
        localStorage.setItem('token', response.token);

        // Update auth context
        login(response.user, response.token);

        console.log('Login successful, redirecting to dashboard...');
        navigate('/dashboard');
      } else {
        setError('Invalid username or password');
      }
    } catch (err) {
      console.error('Login error:', err);
      if (err.response?.data?.error) {
        setError(err.response.data.error);
      } else if (err.message === 'Network Error') {
        setError('Cannot connect to server. Please check if the server is running.');
      } else {
        setError('Login failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleLogin();
    }
  };

  return (
    <ModernBackground variant="auth">
      <Box sx={{
        // Modern viewport units for better mobile support
        height: { xs: 'max(100vh, 100svh)', sm: '100vh' },
        minHeight: { xs: '100svh', sm: '100vh' },
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        p: { xs: 1, sm: 2 },
        position: 'relative',
        zIndex: 1,
        width: '100%',
        overflow: 'hidden'
      }}>
        <ModernCard 
          variant="glass" 
          hover={false}
          sx={{ 
            maxWidth: { xs: 'calc(100% - 16px)', sm: 420 }, 
            width: '100%',
            mx: { xs: 'auto', sm: 0 },
            p: { xs: 1, sm: 2 },
            position: 'relative',
            zIndex: 2,
            pointerEvents: 'auto',
            maxHeight: { xs: 'fit-content', sm: 'none' },
            overflow: 'visible'
          }}
        >
          <Box sx={{ p: { xs: 1.5, sm: 4 }, display: 'flex', flexDirection: 'column', gap: { xs: 1.5, sm: 3 } }}>
            <Box sx={{ textAlign: 'center', mb: 2 }}>
              <Typography 
                variant="h3" 
                sx={{ 
                  mb: 1,
                  fontWeight: 700,
                  background: 'linear-gradient(135deg, #1e40af 0%, #10b981 100%)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  backgroundClip: 'text'
                }}
              >
                🔧 CollisionOS
              </Typography>
              <Typography variant="h6" sx={{ color: 'text.secondary', fontWeight: 500 }}>
                Auto Body Shop Management
              </Typography>
            </Box>

            {error && (
              <Alert 
                severity="error" 
                sx={{ 
                  borderRadius: 2,
                  backdropFilter: 'blur(10px)'
                }}
              >
                {error}
              </Alert>
            )}

            <TextField 
              label="Username" 
              fullWidth
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="admin"
              variant="outlined"
              sx={{
                '& .MuiOutlinedInput-root': {
                  borderRadius: '12px',
                  transition: 'all 0.2s ease',
                  '&:hover': {
                    boxShadow: '0 4px 12px rgba(30, 64, 175, 0.1)'
                  },
                  '&.Mui-focused': {
                    boxShadow: '0 4px 20px rgba(30, 64, 175, 0.2)'
                  }
                }
              }}
            />

            <TextField 
              label="Password" 
              type="password" 
              fullWidth
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="admin123"
              variant="outlined"
              sx={{
                '& .MuiOutlinedInput-root': {
                  borderRadius: '12px',
                  transition: 'all 0.2s ease',
                  '&:hover': {
                    boxShadow: '0 4px 12px rgba(30, 64, 175, 0.1)'
                  },
                  '&.Mui-focused': {
                    boxShadow: '0 4px 20px rgba(30, 64, 175, 0.2)'
                  }
                }
              }}
            />

            <Button 
              variant="contained" 
              onClick={handleLogin}
              disabled={loading}
              size="large"
              fullWidth
              sx={{ 
                py: 1.5,
                fontSize: '1rem',
                fontWeight: 600,
                mt: 1,
                background: 'linear-gradient(135deg, #1e40af 0%, #10b981 100%)',
                '&:hover': {
                  background: 'linear-gradient(135deg, #1e3a8a 0%, #059669 100%)',
                  transform: 'translateY(-1px)',
                  boxShadow: '0 8px 25px rgba(30, 64, 175, 0.3)'
                },
                '&:active': {
                  transform: 'translateY(0px)'
                },
                transition: 'all 0.2s ease',
                position: 'relative',
                zIndex: 10
              }}
              startIcon={loading ? <CircularProgress size={20} color="inherit" /> : null}
            >
              {loading ? 'Signing In...' : 'Login'}
            </Button>

            <ModernCard 
              variant="glass" 
              sx={{ 
                p: 2,
                mt: 2,
                background: (theme) => theme.palette.mode === 'dark' 
                  ? 'rgba(255, 255, 255, 0.05)' 
                  : 'rgba(0, 0, 0, 0.02)'
              }}
            >
              <Typography variant="caption" display="block" sx={{ fontWeight: 600, mb: 1 }}>
                Demo Accounts:
              </Typography>
              <Typography variant="caption" display="block" sx={{ opacity: 0.8 }}>
                admin / admin123 (Owner)
              </Typography>
              <Typography variant="caption" display="block" sx={{ opacity: 0.8 }}>
                manager / manager123 (Manager)
              </Typography>
              <Typography variant="caption" display="block" sx={{ opacity: 0.8 }}>
                estimator / estimator123 (Estimator)
              </Typography>
            </ModernCard>
          </Box>
        </ModernCard>
      </Box>
    </ModernBackground>
  );
}

