const { test, expect } = require('@playwright/test');

test.describe('Login Page Debug Tests', () => {
  test.beforeEach(async ({ page }) => {
    // Start the application
    await page.goto('http://localhost:3000/login');
    await page.waitForLoadState('networkidle');
  });

  test('Debug text visibility issues', async ({ page }) => {
    // Take initial screenshot
    await page.screenshot({ path: 'debug-screenshots/01-login-page-loaded.png', fullPage: true });

    // Check if login elements exist
    const usernameField = page.locator('input[placeholder="admin"]');
    const passwordField = page.locator('input[placeholder="admin123"]');
    const loginButton = page.locator('button:has-text("Login")');

    // Verify elements are present
    await expect(usernameField).toBeVisible();
    await expect(passwordField).toBeVisible();
    await expect(loginButton).toBeVisible();

    // Check computed styles for text visibility
    const usernameStyles = await usernameField.evaluate((el) => {
      const styles = window.getComputedStyle(el);
      return {
        color: styles.color,
        backgroundColor: styles.backgroundColor,
        visibility: styles.visibility,
        opacity: styles.opacity,
        zIndex: styles.zIndex
      };
    });

    const passwordStyles = await passwordField.evaluate((el) => {
      const styles = window.getComputedStyle(el);
      return {
        color: styles.color,
        backgroundColor: styles.backgroundColor,
        visibility: styles.visibility,
        opacity: styles.opacity,
        zIndex: styles.zIndex
      };
    });

    const buttonStyles = await loginButton.evaluate((el) => {
      const styles = window.getComputedStyle(el);
      return {
        color: styles.color,
        backgroundColor: styles.backgroundColor,
        visibility: styles.visibility,
        opacity: styles.opacity,
        zIndex: styles.zIndex,
        pointerEvents: styles.pointerEvents
      };
    });

    console.log('Username field styles:', usernameStyles);
    console.log('Password field styles:', passwordStyles);
    console.log('Login button styles:', buttonStyles);

    // Check for overlapping elements that might block visibility
    const overlays = await page.locator('*').evaluateAll((elements) => {
      return elements
        .filter(el => {
          const styles = window.getComputedStyle(el);
          return (
            styles.position === 'fixed' || 
            styles.position === 'absolute'
          ) && (
            parseInt(styles.zIndex) > 10 ||
            styles.zIndex === 'auto'
          );
        })
        .map(el => ({
          tagName: el.tagName,
          className: el.className,
          position: window.getComputedStyle(el).position,
          zIndex: window.getComputedStyle(el).zIndex,
          pointerEvents: window.getComputedStyle(el).pointerEvents,
          bounds: el.getBoundingClientRect()
        }));
    });

    console.log('Potentially blocking overlays:', overlays);
  });

  test('Debug click interaction issues', async ({ page }) => {
    // Test clicking on username field
    const usernameField = page.locator('input[placeholder="admin"]');

    // Check if element is clickable
    const isClickable = await usernameField.evaluate((el) => {
      const rect = el.getBoundingClientRect();
      const elementAtPoint = document.elementFromPoint(
        rect.left + rect.width / 2,
        rect.top + rect.height / 2
      );
      return {
        targetElement: el.tagName + '.' + el.className,
        elementAtPoint: elementAtPoint?.tagName + '.' + elementAtPoint?.className,
        isTarget: elementAtPoint === el,
        styles: {
          pointerEvents: window.getComputedStyle(el).pointerEvents,
          zIndex: window.getComputedStyle(el).zIndex,
          position: window.getComputedStyle(el).position
        }
      };
    });

    console.log('Username field clickability check:', isClickable);

    // Try different click methods
    await page.screenshot({ path: 'debug-screenshots/02-before-username-click.png', fullPage: true });

    try {
      await usernameField.click({ timeout: 5000 });
      console.log('✓ Standard click worked on username field');
    } catch (error) {
      console.log('✗ Standard click failed on username field:', error.message);

      try {
        await usernameField.click({ force: true });
        console.log('✓ Force click worked on username field');
      } catch (forceError) {
        console.log('✗ Force click also failed on username field:', forceError.message);
      }
    }

    // Test tab navigation
    await page.keyboard.press('Tab');
    const focusedElement = await page.evaluate(() => document.activeElement?.tagName);
    console.log('Element focused after Tab:', focusedElement);

    await page.screenshot({ path: 'debug-screenshots/03-after-tab-navigation.png', fullPage: true });
  });

  test('Debug input reset behavior', async ({ page }) => {
    const usernameField = page.locator('input[placeholder="admin"]');

    // Test typing behavior
    await page.screenshot({ path: 'debug-screenshots/04-before-typing.png', fullPage: true });

    // Try to focus and type
    await usernameField.focus();
    await page.keyboard.type('a');

    let value = await usernameField.inputValue();
    console.log('Value after typing "a":', value);

    await page.screenshot({ path: 'debug-screenshots/05-after-typing-a.png', fullPage: true });

    // Type another character
    await page.keyboard.type('d');
    value = await usernameField.inputValue();
    console.log('Value after typing "ad":', value);

    await page.screenshot({ path: 'debug-screenshots/06-after-typing-ad.png', fullPage: true });

    // Check if any event listeners are interfering
    const eventInfo = await page.evaluate(() => {
      const inputs = document.querySelectorAll('input');
      return Array.from(inputs).map(input => {
        const listeners = getEventListeners ? getEventListeners(input) : 'N/A';
        return {
          placeholder: input.placeholder,
          value: input.value,
          listeners: listeners,
          eventHandlers: {
            onChange: input.onchange?.toString() || 'none',
            onInput: input.oninput?.toString() || 'none',
            onKeyDown: input.onkeydown?.toString() || 'none'
          }
        };
      });
    });

    console.log('Input event information:', eventInfo);

    // Test form submission behavior
    const loginButton = page.locator('button:has-text("Login")');

    await page.screenshot({ path: 'debug-screenshots/07-before-login-click.png', fullPage: true });

    try {
      await loginButton.click();
      console.log('✓ Login button clicked successfully');
    } catch (error) {
      console.log('✗ Login button click failed:', error.message);
    }

    await page.screenshot({ path: 'debug-screenshots/08-after-login-click.png', fullPage: true });

    // Check final input values
    const finalValue = await usernameField.inputValue();
    console.log('Final username value:', finalValue);
  });

  test('Comprehensive UI state analysis', async ({ page }) => {
    // Get comprehensive page state
    const pageState = await page.evaluate(() => {
      return {
        url: window.location.href,
        title: document.title,
        bodyClasses: Array.from(document.body.classList),
        rootElement: {
          styles: window.getComputedStyle(document.getElementById('root')),
          pointerEvents: window.getComputedStyle(document.getElementById('root')).pointerEvents
        },
        allInteractiveElements: Array.from(document.querySelectorAll('button, input, a, [role="button"]')).map(el => ({
          tagName: el.tagName,
          type: el.type || 'N/A',
          placeholder: el.placeholder || 'N/A',
          text: el.textContent?.trim() || 'N/A',
          styles: {
            pointerEvents: window.getComputedStyle(el).pointerEvents,
            zIndex: window.getComputedStyle(el).zIndex,
            position: window.getComputedStyle(el).position,
            visibility: window.getComputedStyle(el).visibility,
            opacity: window.getComputedStyle(el).opacity
          }
        }))
      };
    });

    console.log('Comprehensive page state:', JSON.stringify(pageState, null, 2));

    await page.screenshot({ path: 'debug-screenshots/09-full-page-analysis.png', fullPage: true });
  });
});

